import 'package:flutter/material.dart';
import 'package:flutter_application_1/pages/root_app.dart';
import 'package:http/http.dart' as http;
import 'package:flutter_session/flutter_session.dart';
import 'dart:convert';
import 'package:jiffy/jiffy.dart';
import 'dart:typed_data';




class Notif1 extends StatefulWidget {
  @override
  Notif1State createState() => Notif1State();
}

class Notif1State extends State<Notif1> {
String base64Default = "";

  String username;
  List<dynamic> homeData;
  Uint8List profilePic;
  bool isLoading = true;
  bool isEmpty;

  Future<void> fetchData() async {
    var user = await FlutterSession().get("username");
    setState(() {
      username = user;
    });
    getData();
  }

  Future getData() async {
    print("masuk");
     String url = 'https://wemeetuntar.000webhostapp.com/Notification.php';
    var data = {"username": username};
    var response = await http.post(Uri.parse(url), body: (data));
    print(response.body);
    if(response.body == ""){
      setState(() {
        isEmpty = true;
        isLoading = false;
      });
    }
    else {
    var responseHome = json.decode(response.body);
      setState(() {
          homeData = responseHome;
          isLoading = false;
          isEmpty = false;
      });
    }
  }

  void initState() {
    fetchData();
    super.initState();
  }

  @override
  Widget build(BuildContext context) {
    return new Scaffold(
        backgroundColor: Colors.white,
        appBar: new AppBar(
          backgroundColor: Colors.white,
          leading: new IconButton(
              color: Colors.black,
              icon: Icon(Icons.keyboard_arrow_left),
              onPressed: () {
                Navigator.push(
                  context,
                  MaterialPageRoute(
                    builder: (context) {
                      return RootApp();
                    },
                  ),
                );
              }),
          title: Text(
            'Notifications',
            style: TextStyle(color: Colors.black),
          ),
        ),       
        body: getBody(),
    );
  }

  Widget getBody() {
    Uint8List image;
    return SingleChildScrollView(
      child: isLoading == false ?
      isEmpty == false ?
      Column(
        children: <Widget>[
          Column(
            children: List.generate(homeData.length, (index) {
               if(homeData[index]['ProfilePic'] != null && homeData[index]['ProfilePic'] != ""){
                image = base64.decode(homeData[index]['ProfilePic']);
              }
              else {
                image = base64.decode(base64Default);
              }
              return PostItem(
                image: image,
                time: Jiffy(homeData[index]['DateTime']).fromNow(),
                desc: homeData[index]['NotificationDesc']
              );
            }),
          )
        ],
      )
      : Center(
        child: Container(
          padding: EdgeInsets.only(top: 300),
          child: Text("Your notification is empty",
            style: TextStyle(fontSize: 25))
        )
      )
      : Container (
        height: 600,
        child:
          Center (
            child: CircularProgressIndicator(),
          )
      ),
    );
  }
}

