class PostItem extends StatelessWidget {
  Uint8List image;
  String time;
  String desc;

  PostItem({
    Key key,
    this.image,
    this.time,
    this.desc,
  }) : super(key: key);

  @override
  Widget build(BuildContext context) {
    return Padding(
      padding: const EdgeInsets.all(0.0),
      child: ListTile(
        leading:
          Container(
                    width: 40,
                    height: 40,
                    decoration: BoxDecoration(
                      shape: BoxShape.circle,
                    ),
                    child: Image.memory(
                      image,
                      width: 40,
                      height: 40,
                    )
                  ),
        title: Text(desc),
        subtitle: Text(time),
      ),
    );
  }
}